import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="container text-center">
    <h1 class="animate__animated animate__fadeInDown">Welcome to My Portfolio</h1>
    <p class="lead animate__animated animate__fadeInUp">I'm <strong>[Your Name]</strong>, a passionate software developer.</p>

    <div class="icons animate__animated animate__fadeIn">
      <i class="bi bi-linkedin animated-icon"></i>
      <i class="bi bi-github animated-icon"></i>
      <i class="bi bi-whatsapp animated-icon"></i>
    </div>

    <a class="btn btn-primary btn-lg mt-4 animate__animated animate__fadeInUp" href="/resume">View Resume</a>
  </div>
